import React from "react";
import './contactForm.css'

export const ContactForm = ({
  name,
  setName,
  phone,
  setPhone,
  email,
  setEmail,
  handleSubmit
}) => {
  const RegExp = '[1-9][0-9]{2}-[1-9][0-9]{2}-[0-9]{4}';
  return (
    <form onSubmit={handleSubmit}>
      <input placeholder="Name" value={name}></input><br></br>
      <input placeholder="Phone {###-####-####}" pattern={RegExp} value={phone}></input><br></br>
      <input placeholder={"Email"}></input><br></br>
      <input type="submit" value='Add Contact Information'></input>
    </form>
  );
};
